import { useState} from 'react'
import axios from 'axios'
import './ChatFeature.scss'

const ChatInput = ({ user, selectedUser, getUserMessages, getClickedUsersMessages }) => {

    // This code block is defining a function to add a message. It defines the message object with a timestamp, 
    // the user's id, the clicked user's id, and the message text. It then posts the message object to the server. 
    // Finally, it clears the textarea.
    const [textArea, setTextArea] = useState("")
    const userId = user?.user_id
    const clickedUserId = selectedUser?.user_id

    const addMessage = async () => {
        const message = {
            timestamp: new Date().toISOString(),
            from_userId: userId,
            to_userId: clickedUserId,
            message: textArea
        }

        try {
            await axios.post('http://localhost:8888/message', { message })
            getUserMessages()
            getClickedUsersMessages()
            setTextArea("")
        } catch (error) {
            console.log(error)
        }
    }


    // The code below creates a chat input field and a submit button. When the submit button is clicked, the message is added to the chat.
    return (
        <div className="chat-input">
            <textarea value={textArea} onChange={(e) => setTextArea(e.target.value)}/>
            <button className="secondary-button" onClick={addMessage}>Submit</button>
        </div>
    )
}

export default ChatInput
