import TinderCard from 'react-tinder-card'
import {useEffect, useState} from 'react'
import ChatContainer from '../components/ChatContainer'
import {useCookies} from 'react-cookie'
import axios from 'axios'

const Dashboard = () => {
    const [user, setUser] = useState(null)
    const [genderedUsers, setGenderedUsers] = useState(null)
    const [lastDirection, setLastDirection] = useState()
    const [cookies, setCookie, removeCookie] = useCookies(['user'])

    const userId = cookies.UserId

    const characters = [
        {
            name: 'Monica',
            url: 'https://upload.wikimedia.org/wikipedia/en/6/6c/Monica_Geller.png'
        },
        {
            name: 'Joey',
            url: 'https://upload.wikimedia.org/wikipedia/en/thumb/9/98/Joey_Tribbiani.jpg/220px-Joey_Tribbiani.jpg'
        },
        {
            name: 'Chandler',
            url: 'https://upload.wikimedia.org/wikipedia/en/thumb/6/64/Chandler_Bing.png/220px-Chandler_Bing.png'
        }
    ]


    const getUser = async () => {
        try {
            const response = await axios.get('http://localhost:8000/user', {
                params: {userId}
            })
            setUser(response.data)
        } catch (error) {
            console.log(error)
        }
    }
    const getGenderedUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8000/gendered-users', {
                params: {gender: user?.gender_interest}
            })
            setGenderedUsers(response.data)
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        getUser()

    }, [])

    useEffect(() => {
        if (user) {
            getGenderedUsers()
        }
    }, [user])

    const updateMatches = async (matchedUserId) => {
        try {
            await axios.put('http://localhost:8000/addmatch', {
                userId,
                matchedUserId
            })
            getUser()
        } catch (err) {
            console.log(err)
        }
    }


    const swiped = (direction, swipedUserId) => {
        if (direction === 'right') {
            updateMatches(swipedUserId)
        }
        setLastDirection(direction)
    }

    const outOfFrame = (name) => {
        console.log(name + ' left the screen!')
    }

    const matchedUserIds = user?.matches.map(({user_id}) => user_id).concat(userId)

    const filteredGenderedUsers = genderedUsers?.filter(genderedUser => !matchedUserIds.includes(genderedUser.user_id))


    console.log('filteredGenderedUsers ', filteredGenderedUsers)
    return (
            <div className="dashboard">
                <ChatContainer user={user}/>
                <div className="swipe-container">
                    <div className="card-container">
                        {filteredGenderedUsers?.map((character) => (
                            <TinderCard
                                className="swipe"
                                key={character.user_id}
                                preventSwipe={['up', 'down']}
                                onSwipe={(dir) => swiped(dir, character.user_id)}
                                onCardLeftScreen={() => outOfFrame(character.name)}
                            >   
                                <div
                                    style={{backgroundImage: `url(${character.image_url})`}}
                                    className="card"
                                >
                                    <h3>{character.name}</h3>
                                </div>
                            </TinderCard>
                        ))}
                        <div className="swipe-info">
                            {lastDirection ? <p>You swiped {lastDirection}</p> : <p/>}
                        </div>
                    </div>
                </div>
            </div>
    )
}
export default Dashboard
