    // },
    // {
    //   name: 'Dee',
    //   url: 'https://i.pinimg.com/originals/8b/0d/0d/8b0d0d9b8f3f3f9f9c8f9f9f9f9f9f9f.jpg'
    // },
  ]


  const [lastDirection , setLastDirection] = useState()
  const [current, setCurrent] = useState(0)

  const swipeRight = () => {
    setCurrent(current === characters.length - 1 ? 0 : current + 1)
