        <div className='card_container'>
          {characters.map((character, index) => (
            <div className={index === current ? 'card active' : 'card'} key={index}>
              {index === current && (
                <div className='card_content'>
                  <img src={character.url} alt={character.name}/>
                  <h2>{character.name}</h2>
                </div>
