import {useState} from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AuthModal = ({setShowModal, isSignup, setIsSignUp}) => {


    const [email, setEmail] = useState(null);
    const [password, setPassword] = useState(null);
    const [confirmPassword, setConfirmPassword] = useState(null);
    const [error, setError] = useState(null);

    let navigate = useNavigate();


    console.log(email, password, confirmPassword);
    const handleClick = () => {
        setShowModal(false);
    }

    const handleSubmit =  async (e) => {
        e.preventDefault();
        try {
            if (isSignup && (password !== confirmPassword)) {
                setError("Passwords do not match");
                return
            } else {
                const response = axios.post('http://localhost:8000/signup', { email, password });
                const success = response.status === 201;

                if (success) navigate('/onboarding');
            }
        } catch (err) {
            console.log(err);
        }
    }

    return (
        <div className="auth_modal">
            <div className='close_icon'
                onClick={handleClick}>X</div>
            <h2>{
                isSignup ? 'CREATE ACCOUNT' : 'LOG IN'
            }</h2>
            <p>By clicking Log In, you agree to our terms. Learn how we process your data in our Privacy Policy and Cookie Policy.</p>
            <form onSubmit={handleSubmit}>
                <input type="email" placeholder="Email" id="email" name="email"
                    required={true}
                    onChange={
                        (e) => setEmail(e.target.value)
                    }/>
                <input type="password" placeholder="Password" id="password" name="password"
                    required={true}
                    onChange={
                        (e) => setPassword(e.target.value)
                    }/> {
                isSignup && <input type="password-check" placeholder="Confirm Password" id="password-check" name="password-check"
                    required={true}
                    onChange={
                        (e) => setConfirmPassword(e.target.value)
                    }/>
            }
                <input className="secondary_button" type="submit"/>
                <p>{error}</p>
            </form>
            <hr/>
            <h2>GET THE APP</h2>
        </div>
    )
}

export default AuthModal
