import React from 'react'
import Nav from '../components/Navigation/Nav.js'
import AuthModal from '../components/Authentication/AuthPopup'
import {useState} from 'react'
import {useCookies} from "react-cookie"
import AuthPopup from '../components/Authentication/AuthModal.js'

const Home = () => {
    const [showPopup, setShowPopup] = useState(false)
    const [isSignUp, setIsSignUp] = useState(true)
    const [cookies, setCookie, removeCookie] = useCookies(['user'])
    const authToken = cookies.AuthToken

    const handleClick = () => {
        if (authToken) {
            removeCookie('UserId', cookies.UserId)
            removeCookie('AuthToken', cookies.AuthToken)
            window.location.reload()
            return
        }
        setShowPopup(true)
        setIsSignUp(true)
    }

    return (
        <div className="overlay">
            <Nav
                authToken={authToken}
                minimal={false}
                setShowPopup={setShowPopup}
                showPopup={showPopup}
                setIsSignUp={setIsSignUp}
            />
            <div className="home">
                <h1 className="home-title">We help you find your furever home.</h1>
                <button className="home-button" onClick={handleClick}>
                    {authToken ? 'Signout' : 'Create Account'}
                </button>


                {showPopup && (
                    <AuthPopup setShowPopup={setShowPopup} isSignUp={isSignUp}/>
                )}
            </div>
        </div>
    )
}
export default Home
