import React from 'react'
import Nav from '../components/Navigation/Nav.js'
import AuthPopup from '../components/Authentication/AuthPopup'
import {useState} from 'react'
import {useCookies} from "react-cookie"

const Home = () => {
    const [showPopup, setShowPopup] = useState(false)
    const [isSignUpUser, setisSignUpUser] = useState(true)
    const [cookies, setCookie, removeCookie] = useCookies(['user'])
    const authToken = cookies.AuthToken

    const handleClick = () => {
        if (authToken) {
            removeCookie('UserId', cookies.UserId)
            removeCookie('AuthToken', cookies.AuthToken)
            window.location.reload()
            return
        }
        setShowPopup(true)
        setisSignUpUser(true)
    }

    return (
        <div className="home-container">
            <Nav
                authToken={authToken}
                setShowPopup={setShowPopup}
                showPopup={showPopup}
                setisSignUpUser={setisSignUpUser}
            />
            <div className="home">
                <h1 className="home-title">We help you find your furever home.</h1>
                <button className="home-button" onClick={handleClick}>
                    {authToken ? 'Signout' : 'Create Account'}
                </button>


                {showPopup && (
                    <AuthPopup setShowPopup={setShowPopup} isSignUpUser={isSignUpUser}/>
                )}
            </div>
        </div>
    )
}
export default Home
